// 生成指定范围内的随机数
function getRandomInt(min, max) {
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

function ajax(options) {
  var xhr = null;
  var params = options.data;
  //创建对象
  if(window.XMLHttpRequest) {
    xhr = new XMLHttpRequest()
  } else {
    xhr = new ActiveXObject("Microsoft.XMLHTTP");
  }

  switch(options.type) {
    case 'GET':
      xhr.open(options.type, options.url + "?" + params, options.async);
      xhr.send(null);
      break;
    case 'POST':
      xhr.open(options.type, options.url, options.async);
      xhr.setRequestHeader("Content-Type", "application/json; charset=utf-8");
      xhr.setRequestHeader("Authori-zation", `Bearer ${token}`);
      xhr.send(JSON.stringify(params));
      break;
    default:
      break;
  }

  xhr.onreadystatechange = function() {
    if(xhr.readyState == 4 && xhr.status == 200) {
      options.success(xhr.responseText);
    }
  }
}




// 将Object 改装成以 & 符号连接的字符串
function toParams(param) {
  var result = ""
  for(let name in param) {
    if(typeof param[name] != 'function') {
      result += "&" + name + "=" + encodeURI(param[name]);
    }
  }

  return result.substring(1)
}

//set session
function setSen(k, val) {
  if(typeof val == 'string') {
    sessionStorage.setItem(k, val);
    return val;
  }
  sessionStorage.setItem(k, JSON.stringify(val));
  return val;
}

//get session
function getSen(k) {
  let uu = sessionStorage.getItem(k);

  try {
    if(typeof JSON.parse(uu) != 'number') {
      uu = JSON.parse(uu);
    }
  } catch(e) { }
  return uu;
}
//set local
function setLoc(k, val) {
  if(typeof val == 'string') {
    localStorage.setItem(k, val);
    return val;
  }
  localStorage.setItem(k, JSON.stringify(val));
  return val;
}

//get local
